import { createSlice } from "@reduxjs/toolkit";
import { SelectedState } from "../types";

const initialState: SelectedState = {
  selected: [],
};

export const selectedSlice = createSlice({
  name: "selected",
  initialState,
  reducers: {
    addSelected: (state, action) => {
      const newSelected = action.payload;
      const existingSelected = state.selected.find(
        (selected) => selected.countryCode === newSelected.countryCode
      );
      if (!existingSelected) {
        state.selected.push(action.payload);
      }
    },
    removeSelected: (state, action) => {
      const countryCode = action.payload;
      state.selected = state.selected.filter(
        (selected) => selected.countryCode !== countryCode
      );
    },
  },
});

export const { addSelected, removeSelected } = selectedSlice.actions;

export default selectedSlice.reducer;
