import React from "react";
import { useState } from "react";
import { useDispatch } from "react-redux";

import { Region } from "../src/types";
import { useFetch } from "./hooks/useFetch";
import { getCountries } from "./store/countrySlice";

import "./App.css";
import Countries from "./components/Countries";
import SelectedCountries from "./components/SelectedCountries";

// const regions: Region[] = [
//   { value: "Africa" },
//   { value: "Americas" },
//   { value: "Asia" },
//   { value: "Europe" },
//   { value: "Oceania" },
// ];

const App: React.FC = () => {
  const { countriesList, loading, error } = useFetch(
    "https://restcountries.com/v2/all/"
  );

  const dispatch = useDispatch();
  dispatch(getCountries(countriesList));

  //const [selectedRegion, setSelectedRegion] = useState("");

  return (
    <>
      <div className="App">
        <header className="App-header">
          <p>Countries App </p>
        </header>
        <div className="container">
          {loading && <p>Loading data... Please wait</p>}
          {error && <p>Ops...</p>}
          <section className="selection">
            <Select
              placeholder={"Please select a region of the globe ..."}
              options={regions}
              defaultMenuIsOpen={true}
              getOptionLabel={(regions) => regions.value}
              onChange={(opt) => {
                if (opt != null) {
                  setSelectedRegion(opt.value);
                }
              }}
            />
          </section>
          {selectedRegion && <Countries selectedRegion={selectedRegion} />}
        </div>
        <SelectedCountries />
      </div>
    </>
  );
};

export default App;
