import React, { useEffect } from "react";
import { store } from "../store/store";
import CountriesList from "./CountriesList";
import Select from "react-select";
import { Region } from "../types";
import { useState } from "react";
import { useDispatch } from "react-redux";

import { useFetch } from "../hooks/useFetch";
import { getCountries } from "../store/countrySlice";
import "./Countries.css";

// interface Props {
//   selectedRegion: string;
// }

const regions: Region[] = [
  { value: "Africa" },
  { value: "Americas" },
  { value: "Asia" },
  { value: "Europe" },
  { value: "Oceania" },
];

const Countries = () => {
  const { allCountries, loading, error } = useFetch(
    "https://restcountries.com/v2/all/"
  );

  const dispatch = useDispatch();
  dispatch(getCountries(allCountries));

  useEffect;

  const [selectedRegion, setSelectedRegion] = useState("");

  const countriesList = store
    .getState()
    .countries.countries.filter((country) => country.region === selectedRegion);

  return (
    <section>
      <section className="selection">
        <Select
          placeholder={"Please select a region of the globe ..."}
          options={regions}
          defaultMenuIsOpen={true}
          getOptionLabel={(regions) => regions.value}
          onChange={(opt) => {
            if (opt != null) {
              setSelectedRegion(opt.value);
            }
          }}
        />
      </section>

      {selectedRegion && (
        <CountriesList
          countries={countriesList}
          selectedRegion={selectedRegion}
          selectCountry="add"
        />
      )}
    </section>
  );
};

export default Countries;
