import { useState, useEffect } from "react";

export const useFetch = (url: string) => {
  const [countriesList, setCountriesList] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchCountries = async () => {
    try {
      setLoading(true);
      const response = await fetch(url);
      const countries = await response.json();
      if (countries) {
        const countryInfo = countries.map((info: any) => {
          const {
            alpha3Code,
            name,
            capital,
            flag,
            region,
            population,
            languages,
            borders,
            currencies,
          } = info;

          return {
            alpha3Code,
            name,
            capital,
            flag,
            region,
            population,
            languages,
            borders,
            currencies,
          };
        });
        setCountriesList(countryInfo);
      } else {
        setCountriesList([]);
        setLoading(false);
      }
      setLoading(false);
    } catch (error: any) {
      setLoading(false);
      setError(error);
    }
  };

  useEffect(() => {
    fetchCountries();
  }, []);

  return { countriesList, loading, error };
};
